import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import astronautImg from "../images/004-astronaut.svg";

export const Information = () => {
  const allAstronauts = useSelector((store) => store.astronauts.all.people);
  const [checkISS, setCheckISS] = useState(0);
  let height;
  let width;

  const checkLocation = () => {
    allAstronauts.map((astronaut) => {
      if (astronaut.craft === "ISS") {
        setCheckISS(checkISS + 1);
      }
    });
    return checkISS;
  };

  const getRandomIntInclusive = (min, max, val) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    const random = `${Math.floor(Math.random() * (max - min + 1)) + min}${val}`;
    return random;
  };

  return (
    <>
      {allAstronauts.map((astronaut, index) => (
        <AstronautContaner key={astronaut.name}>
          {(height = getRandomIntInclusive(10, 30, "vh"))}
          {(width = getRandomIntInclusive(10, 30, "vw"))}
          <Img
            positionTop={height}
            positionLeft={width}
            src={astronautImg}
            alt="Cartoon astronaut"
          />
          <P>{astronaut.name}</P>
        </AstronautContaner>
      ))}
    </>
  );
};

const floating = keyframes`
from {
  transform: rotate(0deg);
}

to {
  transform: rotate(360deg);
}`;

const AstronautContaner = styled.div`
  width: 100px;
  position: relative;
  top: ${(props) => props.positionTop};
  left: ${(props) => props.positionLeft};
  animation: ${floating} 45s linear infinite;
`;

const Img = styled.img`
  width: inherit;
`;
const P = styled.p`
  color: #fff;
`;
