import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import astronautImg from "../images/004-astronaut.svg";

export const Information = () => {
  const allAstronauts = useSelector((store) => store.astronauts.all.people);
  const [checkISS, setCheckISS] = useState(0);

  const checkLocation = () => {
    allAstronauts.map((astronaut) => {
      if (astronaut.craft === "ISS") {
        setCheckISS(checkISS + 1);
      }
    });
    return checkISS;
  };

  return (
    <div>
      <h1>
        {checkISS === allAstronauts.length
          ? "All astronauts are on the International Space Station"
          : ""}
      </h1>
      {allAstronauts.map((astronaut, index) => (
        <AstronautContaner key={astronaut.name}>
          <Img className={index} src={astronautImg} alt="Cartoon astronaut" />
          <P>{astronaut.name}</P>
        </AstronautContaner>
      ))}
    </div>
  );
};

const AstronautContaner = styled.div`
  width: 100px;
`;

const floating = keyframes`
from {
  transform: rotate(0deg);
}

to {
  transform: rotate(360deg);
}`;

const Img = styled.img`
  width: inherit;
  animation: ${floating} 45s linear infinite;
`;
const P = styled.p`
  color: #fff;
`;
