import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { onStart } from "../reducers/astronauts";
import { Information } from "./Information";
import styled from "styled-components";

export const FetchAstronauts = () => {
  const [clicked, setClicked] = useState(false);
  const dispatch = useDispatch();

  const allAstronauts = useSelector((store) => store.astronauts.all);

  const handleOnClick = () => {
    dispatch(onStart());
    setClicked(!clicked);
  };

  return (
    <div>
      {!clicked && (
        <Button onClick={() => handleOnClick()}>get astro facts</Button>
      )}

      {clicked && loading && <Information />}
    </div>
  );
};

const Button = styled.button`
  font-size: 50px;
  color: #fff;
  background: #000;
`;
