import React, { useState } from "react";
import styled, { keyframes } from "styled-components";
import { useSelector } from "react-redux";
import astronautImg from "../images/004-astronaut.svg";

export const Information = () => {
  const allAstronauts = useSelector((store) => store.astronauts.all.people);
  const [checkISS, setCheckISS] = useState(0);
  let height;
  let width;

  const checkLocation = () => {
    allAstronauts.map((astronaut) => {
      if (astronaut.craft === "ISS") {
        setCheckISS(checkISS + 1);
      }
    });
    return checkISS;
  };

  const getRandomInt = (min, max, val) => {
    console.log("hello");
    min = Math.ceil(min);
    max = Math.floor(max);
    const random = `${Math.floor(Math.random() * (max - min + 1)) + min}${val}`;
    return random;
  };

  const getRandomDeg = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    const random = Math.floor(Math.random() * (max - min + 1)) + min;
    if (random % 2 === 0) {
      return "360deg";
    } else {
      return "360deg";
    }
  };

  return (
    <Wrapper>
      {allAstronauts.map((astronaut, index) => (
        <AstronautContainer
          positionTop={height}
          positionLeft={width}
          y={"360deg"}
          key={astronaut.name}
        >
          {(height = getRandomInt(10, 90, "%"))}
          {(width = getRandomInt(10, 90, "%"))}
          <Img src={astronautImg} alt="Cartoon astronaut" />
          <P>{astronaut.name}</P>
        </AstronautContainer>
      ))}
    </Wrapper>
  );
};

const floating = keyframes`
from {
  transform: rotate(0deg);
}

to {
  transform: rotate(-360deg);
}`;

const Wrapper = styled.section`
  padding: 30px;
`;

const AstronautContainer = styled.div`
  width: 100px;
  position: relative;
  margin: 20px;
  top: ${(props) => props.positionTop};
  left: ${(props) => props.positionLeft};
  animation: ${floating} 45s linear infinite;
`;

const Img = styled.img`
  width: inherit;
`;
const P = styled.p`
  color: #fff;
`;
