import React from "react";
import { useSelector } from "react-redux";
import styled, { keyframes } from "styled-components";
import backgroundPattern from "../images/concrete.png";

export const MoreInformation = () => {
  const allAstronauts = useSelector((store) => store.astronauts.all.people);

  const textStyling = (array) => {
    let styledText = "";
    array.forEach((word, index) => {
      if (array.length - index === 1) {
        styledText += `${word.name}`;
      } else if (array.length - index > 2) {
        styledText += `${word.name}, `;
      } else {
        styledText += `${word.name} & `;
      }
    });
    return styledText;
  };

  console.log(allAstronauts);
  return (
    <Main>
      <Text>
        {allAstronauts.map((astronaut) => (
          <Text>{astronaut.name} </Text>
        ))}
      </Text>
    </Main>
  );
};

const Main = styled.section`
  display: flex;
  justify-content: center;
  align-items: center;

  background-color: #020646;
  background-image: radial-gradient(
      #00000099,
      #00000099,
      #00000030,
      #00000099,
      #00000099,
      #00000030,
      #00000099,
      #00000099
    ),
    url(${backgroundPattern});
  background-repeat: repeat;
  background-position: center;

  height: 100vh;
  width: 100vw;

  box-shadow: inset 0px 30px 60px 60px #000000;
`;

const Text = styled.p`
  position: relative;
  z-index: 25;
  color: #e0d6f0;
  margin: 0 auto;
  font-size: 4vw;
  font-weight: bolder;
  font-family: "Righteous", cursive;
  letter-spacing: 5px;
  text-align: center;
  text-shadow: -2px -2px 0px #3b2563, 2px 2px 0px #4a3969;
  width: 70%;
`;
