import React from "react";
import { useSelector } from "react-redux";
import styled, { keyframes } from "styled-components";
import backgroundPattern from "../images/concrete.png";
import img1 from "../images/003-Asteroid.svg";
import img2 from "../images/005-astronomy.svg";
import img3 from "../images/007-comet.svg";
import img4 from "../images/009-earth.svg";
import img5 from "../images/017-moon.svg";
import img6 from "../images/020-orbit.svg";
import img7 from "../images/022-planet.svg";
import img8 from "../images/029-spaceship.svg";
import img9 from "../images/031-star.svg";

export const MoreInformation = () => {
  const images = [img1, img2, img3, img4, img5, img6, img7, img8, img9];
  const allAstronauts = useSelector((store) => store.astronauts.all.people);

  const getRandomInt = (min, max, val) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    const random = `${Math.floor(Math.random() * (max - min + 1)) + min}${val}`;
    return random;
  };

  const getRandomDeg = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    const random = Math.floor(Math.random() * (max - min + 1)) + min;
    if (random % 2 === 0) {
      return "360deg";
    } else {
      return "-360deg";
    }
  };

  const textStyling = (array) => {
    let styledText = "";
    array.forEach((word, index) => {
      if (array.length - index === 1) {
        styledText += `${word.name}`;
      } else if (array.length - index > 2) {
        styledText += `${word.name}, `;
      } else {
        styledText += `${word.name} & `;
      }
    });
    return styledText;
  };

  console.log(allAstronauts);
  return (
    <Main id="more-information">
      <Wrapper>
        <Title>
          {textStyling(allAstronauts)}{" "}
          {allAstronauts.length === 1 ? "is" : "are"} currently in space, here's
          some facts about them.
        </Title>
        {allAstronauts.map((astronaut) => (
          <WrapperRow>
            <Img src={astronaut.biophoto} alt={astronaut.name} />
            <Wrapper>
              <P>
                <Text>Name:</Text>{" "}
                {`
                  ${astronaut.name}
                `}
              </P>
              <P>
                <Text>Title:</Text>{" "}
                {`
                  ${astronaut.title}
                `}
              </P>
              <P>
                <Text>Location:</Text>{" "}
                {`
                  ${
                    astronaut.location === "International Space Station"
                      ? "ISS"
                      : astronaut.location
                  }
                `}
              </P>
              <P>
                <Text>About:</Text>
                {` ${astronaut.bio}`}
              </P>
              <P>
                <Text>{astronaut.twitter !== "" ? "Twitter: " : ""}</Text>
                {astronaut.twitter && (
                  <A href={astronaut.twitter}>
                    Check out {astronaut.name}'s twitter
                  </A>
                )}
              </P>
            </Wrapper>
          </WrapperRow>
        ))}
        {images.map((img) => (
          <ImgContainer
            width="45px"
            zIndex="5"
            opacity="0.7"
            src={img}
            display="none"
            alt="Space cartoon"
            key={getRandomInt(3, 1000000, "")}
            positionTop={getRandomInt(10, 80, "%")}
            positionLeft={getRandomInt(10, 80, "%")}
            y={getRandomDeg(0, 10)}
            a={getRandomInt(10, 90, "%")}
            b={getRandomInt(10, 90, "%")}
            c={getRandomInt(10, 90, "%")}
            d={getRandomInt(10, 90, "%")}
            e={getRandomInt(10, 90, "%")}
          />
        ))}
      </Wrapper>
    </Main>
  );
};

const Main = styled.section`
  display: flex;
  justify-content: center;
  align-items: center;

  background-color: #25074c;
  background-image: radial-gradient(
      #00000099,
      #00000099,
      #00000030,
      #00000099,
      #00000099,
      #00000030,
      #00000099,
      #00000099
    ),
    url(${backgroundPattern});
  background-repeat: repeat;
  background-position: center;

  min-height: 100vh;
  width: 100vw;

  box-shadow: inset 0px -100px 200px 0px #000000,
    inset 0px 100px 200px 0px #000000, inset -100px 0px 200px 0px #000000,
    inset 100px 0px 200px 0px #000000;

  min-height: 100vh;

  @media (max-width: 668px) {
    box-shadow: inset 0px -50px 100px 0px #000000,
      inset 0px 50px 100px 0px #000000, inset -50px 0px 100px 0px #000000,
      inset 50px 0px 100px 0px #000000;
  }
`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  @media (max-width: 668px) {
    width: 100%;
  }
`;

const WrapperRow = styled.div`
  display: flex;
  flex-direction: row;
  margin: 20px;
`;

const Text = styled.span`
  position: relative;
  display: inline-block;
  z-index: 25;
  color: #e0d6f0;
  font-size: 2vw;
  font-weight: bolder;
  font-family: "Righteous", cursive;
  letter-spacing: 5px;
  text-shadow: -1px -1px 0px #3b2563, 1px 1px 0px #4a3969;
  text-decoration: underline;

  @media (max-width: 668px) {
    font-size: 18px;
    letter-spacing: 2px;
    text-shadow: none;
  }
`;

const Title = styled.h1`
  position: relative;
  display: inline-block;
  text-align: center;
  z-index: 25;
  color: #e0d6f0;
  font-size: 2.3vw;
  font-family: "Righteous", cursive;
  letter-spacing: 5px;

  @media (max-width: 668px) {
    font-size: 18px;
    letter-spacing: 2px;
  }
`;

const Img = styled.img`
  height: 150px;
  border-radius: 10px;
  margin-right: 15px;
`;

const P = styled.span`
  display: inline-block;
  text-shadow: none;
  font-size: 1.75vw;

  position: relative;
  display: inline-block;
  z-index: 25;
  color: #e0d6f0;
  font-weight: bolder;
  font-family: "Righteous", cursive;
  letter-spacing: 2px;

  @media (max-width: 668px) {
    font-size: 14px;
    letter-spacing: 2px;
  }
`;

const A = styled.a` display: inline-block;
text-shadow: none;
font-size: 1.75vw;

position: relative;
display: inline-block;
z-index: 25;
color: #e0d6f0;
font-weight: bolder;
font-family: "Righteous", cursive;
letter-spacing: 2px;

@media (max-width: 668px) {
  font-size: 14px;
  letter-spacing: 2px;`;

const rotating = (y) => keyframes`
from {
  transform: rotate(0deg);
}

to {
  transform: rotate(${y});
}`;

const movement = (a, b, c, d, e) => keyframes`
0% {
  left: ${a});
  top: ${e});
}
20% {
  left: ${b};
  top: ${d};
}
40% {
  left: ${d};
   top: ${b};
}
60% {
  left: ${e};
  top: ${c};
}
80% {
  left: ${c};
  top: ${d};
}
100% {
  left: ${a};
  top: ${e};
}
`;

const ImgContainer = styled.img`
  margin: 20px;
  width: ${(props) => props.width};
  position: absolute;
  animation: ${(props) => rotating(props.y)} 45s linear infinite,
    ${(props) => movement(props.a, props.b, props.c, props.d, props.e)} 355s
      linear infinite;
  top: ${(props) => props.positionTop};
  left: ${(props) => props.positionLeft};
  z-index: ${(props) => props.zIndex};
  opacity: ${(props) => props.opacity};

  @media (max-width: 1024px) {
    display: ${(props) => props.display};
  }

  @media (max-width: 668px) {
    width: ${(props) => props.mobileWidth};
  }
`;
